// Created by jurandi on 20-06-2023.\n#include "doTheWorldOne.c"\n\nchar *dumps_tree(){\n    DtwTree *tree = newDtwTree();\n    DtwTree_add_tree_from_hardware(\n            tree,\n            "tests/target",\n            (DtwTreeProps){\n                    .content = DTW_INCLUDE,\n                    .hadware_data=DTW_INCLUDE,\n                    .path_atributes=DTW_INCLUDE\n            }\n    );\n    char *content = DtwTree_dumps_json_tree(\n            tree,\n            (DtwTreeProps){\n                    .minification = DTW_MIMIFY,\n                    .ignored_elements=DTW_HIDE,\n                    .content = DTW_INCLUDE,\n                    .hadware_data=DTW_HIDE,\n                    .path_atributes=DTW_INCLUDE\n            }\n    );\n    DtwTree_free(tree);\n    return content;\n}\nint main(){\n    char * content = dumps_tree();\n    DtwTree *tree = newDtwTree();\n    DtwTree_loads_json_tree(tree,content);\n    DtwTree_represent(tree);\n    DtwTree_free(tree);\n    free(content);\n}