#include "../../doTheWorld.c"

int main(int argc, char *argv[]){

  const char *path = "../exemple_folder/deer.jpg";
  int size;
  bool is_binary;
  //load any file, is useful if you don't know if the file is binary or not
  const char *content = dtw_load_any_content(path,&size,&is_binary);
  if(content == NULL){
    printf("error oppening %s\n",path);
    return 1;
  }
  printf("size: %d\n",size);
  printf("is_binary: %s\n",is_binary ? "true" : "false");
  printf("content: %s\n",content);
  free(content);
  return 0;
}

#include "../../doTheWorld.c"

int main(int argc, char *argv[]){

  //| entity_type return the type of the entity
  //| it can be:
  //| DTW_FILE = 1
  //| DTW_FOLDER = 2
  //| DTW_NOT_FOUND = -1
  const char *not_exist = "not_exist";
  short type = dtw_entity_type(not_exist);
  printf("%s is a %i\n",not_exist,type);

  const char *file = "../exemple_folder/deer.jpg";
  type = dtw_entity_type(file);
  printf("%s is a %i\n",file,type);

  const char *folder = "../exemple_folder";
  type = dtw_entity_type(folder);
  printf("%s is a %i\n",folder,type);



  return 0;
}

